mapper.update:
update
world_main
set main_title =#{main_title},
	 main_content =#{main_content},
	 main_image1 =#{main_image1}	 			 
where main_num =#{main_num}

mapper.select:
insert into world_photo values(world_photo_seq.nextval	
,#{photo_title}
,#{photo_content}
,sysdate		
,#{member_num}
)

mapper.update:
update
world_photo
set photo_title =#{photo_title},
	 photo_content =#{photo_content}	 
where photo_num =#{photo_num}

update world_photoImg
set photo_image1 =#{photo_image1}, photo_image2 =#{photo_image2}, photo_image3 =#{photo_image3}, photo_image4 =#{photo_image4}, photo_image5 =#{photo_image5}
where photo_num =#{photo_num}

mapper.select:
insert into world_board values(world_board_seq.nextval
	,#{board_title}
	,#{board_content}
	,sysdate
	,#{board_count}
	,#{member_num}
	)

mapper.update:
update
world_board	
set board_title =#{board_title},
	 board_content =#{board_content}

where board_num =#{board_num}

mapper.select:
insert into world_reply values(world_reply_seq.nextval	
,#{reply_content}
,sysdate
,#{member_num}
,#{board_num}
)

mapper.update:
update
world_reply
set reply_content =#{reply_content},
 	 board_num =#{board_num}
where reply_num =#{reply_num}
